import React, { useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard';
import axios from 'axios';
import Modal from '../components/Modal';

const Products = () => {
  const [pokemon, setPokemon] = useState([]); // Cambiado para manejar datos de Pokémon
  const [selectedProduct, setSelectedProduct] = useState(null); // Estado para el Pokémon seleccionado
  const [showModal, setShowModal] = useState(false); // Estado para el modal

  useEffect(() => {
    obtenerDatos();
  }, []);

  const obtenerDatos = async () => {
    try {
      const uri = 'https://pokeapi.co/api/v2/pokemon/ditto';
      const response = await axios.get(uri);

      // Ajusta los datos al formato esperado
      const pokemonData = {
        id: response.data.id,
        name: response.data.name,
        image: response.data.sprites.front_default,
        abilities: response.data.abilities.map((ability) => ability.ability.name),
      };

      setPokemon([pokemonData]); // Envuélvelo en un array para usar el método map
    } catch (error) {
      console.error('Error al obtener los datos:', error);
    }
  };

  // Maneja la apertura del modal
  const handleShowModal = (product) => {
    setSelectedProduct(product);
    setShowModal(true);
  };

  // Maneja el cierre del modal
  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedProduct(null);
  };

  return (
    <div className="row">
      {pokemon.map((product) => (
        <div className="col-md-4 mb-4" key={product.id}>
          <ProductCard product={product} onView={() => handleShowModal(product)} />
        </div>
      ))}

      {/* Modal para mostrar los detalles */}
      {selectedProduct && (
        <Modal
          show={showModal}
          onClose={handleCloseModal}
          title={selectedProduct.name}
          image={selectedProduct.image}
        >
          <p><strong>Habilidades:</strong></p>
          <ul>
            {selectedProduct.abilities.map((ability, index) => (
              <li key={index}>{ability}</li>
            ))}
          </ul>
        </Modal>
      )}
    </div>
  );
};

export default Products;
