import React, { useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard';
import axios from 'axios';
import Modal from '../components/Modal';

const Products = () => {
  const [morty, setMorty] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null); // Estado para el personaje seleccionado
  const [showModal, setShowModal] = useState(false); // Estado para el modal

  useEffect(() => {
    obtenerDatos();
  }, []);

  const obtenerDatos = async () => {
  try {
    const uri = 'https://pokeapi.co/api/v2/pokemon/';
    const response = await axios.get(uri);

    // Mapea las URLs de los Pokémon y realiza solicitudes adicionales
    const pokemonDetails = await Promise.all(
      response.data.results.map(async (pokemon) => {
        const details = await axios.get(pokemon.url); // Obtén detalles de cada Pokémon
        return {
          id: details.data.id,
          name: details.data.name,
          image: details.data.sprites.front_default, // Imagen del Pokémon
        };
      })
    );

    setMorty(pokemonDetails); // Actualiza el estado con los detalles completos
  } catch (error) {
    console.error('Error al obtener los datos:', error);
  }
};

};

export default Products;
